mapper.sql:
    	where 1=1 
	    
	    	 AND id = #{id}
	    
	    
	    	 AND student_name = #{studentName}
	    
	    
	    	 AND age = #{age}
	    
	    
	    	 AND telephone = #{telephone}
	    
	    
	    	 AND address = #{address}
	    
	    
	    	 AND email = #{email}
	    
	    
	    	 AND email_show = #{emailShow}
	    
	    
	    	 AND wechat = #{wechat}
	    
	    
	    	 AND wechat_show = #{wechatShow}
	    
	    
	    	 AND qq = #{qq}
	    
	    
	    	 AND qq_show = #{qqShow}
	    
	    
	    	 AND imagePath = #{imagePath}
	    
	    
	    	 AND sex = #{sex}

    	where 1=1 
    	
		  	 AND user.student_id=#{studentId,jdbcType=VARCHAR}
		
		
		  	 AND user.user_name=#{userName,jdbcType=VARCHAR}
		
		
		  	 AND user.password=#{password,jdbcType=VARCHAR}
		
		
		  	 AND user.status=#{status,jdbcType=VARCHAR}
		
		
		  	 AND user.type IN (${type})
		
		
		  	 AND user.isvalid=#{isvalid,jdbcType=CHAR}
		
	    
	    	 AND student.id = #{id}
	    
	    
	    	 AND student.student_name = #{studentName}
	    
	    
	    	 AND student.age = #{age}
	    
	    
	    	 AND student.telephone = #{telephone}
	    
	    
	    	 AND student.address = #{address}
	    
	    
	    	 AND student.email = #{email}
	    
	    
	    	 AND student.email_show = #{emailShow}
	    
	    
	    	 AND student.wechat = #{wechat}
	    
	    
	    	 AND student.wechat_show = #{wechatShow}
	    
	    
	    	 AND student.qq = #{qq}
	    
	    
	    	 AND student.qq_show = #{qqShow}
	    
	    
	    	 AND student.imagePath = #{imagePath}
	    
	    
	    	 AND student.sex = #{sex}

mapper.select:
    select a.* from (
	    select 
	    
	    from student_info student right join user_info user on user.student_id=student.id 
	    
    )a
    
    	
	    order by ${sortName} ${sortOrder}
    	
    
    
    	
	    LIMIT ${start},${pageSize}

